import { useEffect } from "react";
import { useState } from "react"
import Orderlist from "./orderlist";

const Home = () => {
    const [orders,setOrder] = useState([
        {id: 1, name: 'juice', marketName : 'bahdja-shop', client: 'soltani'},
        {id: 2, name: 'fruit', marketName : 'badr-shop', client: 'henni'},
        {id: 3, name: 'k-chire', marketName : 'city-shop', client: 'ghoulam'}
    ])
    const handleDelete = (id) => {
        const newOrderList = orders.filter(order => order.id !== id)
        setOrder(newOrderList)
    }
    useEffect(() => {
        console.log('use effect ran')
    }, [])
    
    return (
        <div className="home">
           <Orderlist orders = { orders } name = "All orders" handleDelete={handleDelete} />
        </div>
    );
}
 
export default Home;
